import React, { useContext, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { ContextGlobal } from '../Components/utils/global.context'; // Ajusta la ruta según tu estructura de carpetas

const Detail = () => {
  const { id } = useParams(); // Obtiene el id de la URL
  const { state } = useContext(ContextGlobal);
  const { dentists, theme } = state;
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserDetail = async () => {
      try {
        const user = dentists.find(d => d.id === parseInt(id));
        if (!user) throw new Error('Usuario no encontrado');
        setUser(user);
        setLoading(false);
      } catch (error) {
        setError(error.message);
        setLoading(false);
      }
    };

    fetchUserDetail();
  }, [id, dentists]);

  if (loading) {
    return <p>Cargando...</p>;
  }

  if (error) {
    return <p>Error: {error}</p>;
  }

  if (!user) {
    return <p>No se encontró el usuario.</p>;
  }

  return (
    <div className={`detail-container ${theme}`}>
      <h2>Detalle del Usuario</h2>
      <p>Nombre: {user.name}</p>
      <p>Username: {user.username}</p>
      <p>Email: {user.email}</p>
      <p>Teléfono: {user.phone}</p>
      <p>Website: {user.website}</p>
    </div>
  );
};

export default Detail;
