import React, { useState } from "react";

const Form = () => {
  const [formData, setFormData] = useState({
    name: "",
    mail: "",
  });

  const [errors, setErrors] = useState({
    name: "",
    mail: "",
  });

  const validateName = (name) => {
    let trimmedName = name.trimStart();
    if (trimmedName.length < 5) {
      return "El nombre debe tener más de 5 caracteres y no puede comenzar con un espacio.";
    }
    return "";
  };

  const validateEmail = (email) => {
    let trimmedEmail = email.trimStart();
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(trimmedEmail)) {
      return "Ingresa un correo electrónico válido.";
    }
    return "";
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Actualizar los datos del formulario
    setFormData({
      ...formData,
      [name]: value,
    });

    // Validar los campos
    if (name === "name") {
      setErrors({
        ...errors,
        name: validateName(value),
      });
    } else if (name === "mail") {
      setErrors({
        ...errors,
        mail: validateEmail(value),
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const nameError = validateName(formData.name);
    const emailError = validateEmail(formData.mail);

    if (nameError || emailError) {
      setErrors({
        name: nameError,
        mail: emailError,
      });
    } else {
      // Procesar el formulario si no hay errores
      console.log("Formulario enviado", formData);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Nombre y apellido</label>
          <input
            type="text"
            name="name"
            id="name"
            value={formData.name}
            onChange={handleChange}
          />
          {errors.name && <p style={{ color: "red" }}>{errors.name}</p>}
        </div>

        <div>
          <label htmlFor="mail">Email</label>
          <input
            type="email"
            name="mail"
            id="mail"
            value={formData.mail}
            onChange={handleChange}
          />
          {errors.mail && <p style={{ color: "red" }}>{errors.mail}</p>}
        </div>

        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default Form;
