import React from 'react';
import { Link } from 'react-router-dom';

const Card = ({ name, username, id, isFav }) => {

  const addFav = () => {
    const favs = JSON.parse(localStorage.getItem('favs')) || [];
    const newFav = { id, name, username };

    if (!favs.some(fav => fav.id === id)) {
      favs.push(newFav);
      localStorage.setItem('favs', JSON.stringify(favs));
      alert(`${name} fue agregado a favoritos!`);
    } else {
      alert(`${name} ya está en favoritos.`);
    }
  };

  const removeFav = (updateFavs) => {
    let favs = JSON.parse(localStorage.getItem('favs')) || [];
    favs = favs.filter(fav => fav.id !== id);
    localStorage.setItem('favs', JSON.stringify(favs));

    updateFavs(favs); // Actualiza la lista de favoritos
    alert(`${name} fue eliminado de favoritos!`);
  };


  return (
    <div className="card">
      <Link to={`/dentist/${id}`}>
        <h3>{name}</h3>
        <p>{username}</p>
        <p>ID: {id}</p>
      </Link>

      {isFav ? (
        <button onClick={removeFav} className="favButton">Remove fav</button>
      ) : (
        <button onClick={addFav} className="favButton">Add fav</button>
      )}
    </div>
  );
};

export default Card;
